query.oql:
select city, sum(select p.T.med from partition p) as N
from (
      select p.city as city, count(p.chartBasedData.medication) as med
      from patients p
     ) T
group by city: T.city

query.qrawl:
select city, sum(select p.T.med from partition p) as N
from (
      select p.city as city, count(p.chartBasedData.medication) as med
      from patients p
     ) T
group by city: T.city

query.oql:
select d.description, d.sub_category, d.category
from patients p,
     p.patientBasedData.diagnosis d

query.qrawl:
select d.description, d.sub_category, d.category
from patients p,
     p.patientBasedData.diagnosis d

